INSERT INTO `job_student` VALUES ('104', '11267055', '董琦峰', 'e10adc3949ba59abbe56e057f20f883e', '软件工程', '11计71', '1', '18252150936', '755745979@qq.com', '江苏省苏州市', '江苏省苏州市相城区黄埭镇东桥长和一村306', '<p>				</p><p><br/></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\"><span style=\"font-size: 16px;\">董琦峰，男，出生于江苏苏州，本科生，就读于江苏师范大学计算机科学与技术学院软件工程专业。<br/></span></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\"><span style=\"font-size: 16px;\">本人性格开朗，为人谦和，工作勤奋，认真负责，</span><tt><span style=\"font-size: 16px;\">具备创新意识、能独立分析和解决问题、具有较好的动手能力,对待工作细致负责。</span></tt><span style=\"font-size: 16px;\">善于学习新技术，肯钻研，肯吃苦，纪律观念极强，为人正直，乐于助人，善于与他人协同工作，容易融入环境。<span style=\"font-size: 16px; font-family: Arial; background-color: white;\">&nbsp;</span><span style=\"font-size: 16px; background-color: white;\">专业知识扎实，有积极的工作态度，能够独立工作，又极赋团队精神，同时具有良好的文化素质；赋有进取心，有良好的职员治理和沟通协调能力。</span></span></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\"><span style=\"font-size: 16px;\">曾经参加过一个项目---30天制作操作系统，虽然最后以失败告终，但是我从中学到了很多，也积累了一定的开发项目的经验；还做过网上考试系统和当当网等项目。</span></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\"><span style=\"font-size: 16px;\">我的目标是成为一名优秀的项目负责人。我将以饱满的热情投入到工作岗位。相信您的信任和我的实力将为我们共同的未来带来共同的成功! </span><span style=\"font-size:12px\">&nbsp; &nbsp;</span></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\">教育经历：<span style=\"font-size: 16px; font-family: 宋体;\">2008/09—2011/06，就读陆慕高级中学；2011/09—2014/06，就读江苏师范大学，并接受达内培训，进行软件开发工程和web开发项目。</span></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\"><span style=\"font-size: 16px; font-family: 宋体;\">证书情况：第一学期获一等奖学金和“三好学生”称号；第二学期获二等奖学金和“优秀学生干部”称号；第三学期获一等奖学金和“三好学生”称号；经过达内培训，获得高级软件工程师、高级web开发工程师证书；获得大学英语四级证书；曾获第五届“蓝桥杯”全国软件和信息技术专业人才大赛江苏赛区JAVA软件开发本科B组三等奖。</span></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\"><span style=\"font-size: 16px; font-family: 宋体;\">职业技能：1.外语能力：CET-4；</span></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\"><span style=\"font-size: 16px;\"><span style=\"font-size: 16px; font-family: 宋体;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2.</span><span style=\"font-size: 12px; font-family: 宋体;\"><span style=\"font-size: 16px; font-family: 宋体; background-color: white;\">熟悉计算机操作，</span><span style=\"font-size: 16px; font-family: 宋体;\"><span style=\"font-family: 宋体; background-color: white;\">熟练使用</span><span style=\"font-family: &#39;Times New Roman&#39;; background-color: white;\">office</span><span style=\"font-family: 宋体; background-color: white;\">系列办公软件及</span><span style=\"font-family: &#39;Times New Roman&#39;; background-color: white;\">Internet</span><span style=\"font-family: 宋体; background-color: white;\">应用；</span></span></span></span></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\"><span style=\"font-size: 16px; font-family: 宋体; background-color: white;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 3.掌握面向对象技术和工程化编程思想以及UML；</span></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\"><span style=\"font-size: 16px; font-family: 宋体; background-color: white;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 4.掌握J2EE构架，特别是MVC编程模型；</span></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\"><span style=\"font-size: 16px; font-family: 宋体; background-color: white;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 5.掌握SQL语言，熟悉SQL Server和Oracle（PL/SQL）数据库设计；</span></p><p style=\"text-align:justify;text-justify:inter-ideograph;text-indent:24px\"><span style=\"font-size: 16px; font-family: 宋体; background-color: white;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 6.熟悉Jsp,servlet,jdbc,html,xml,javascript,java语言</span></p><p><br/></p><p><br/></p><p><br/></p><p>			</p>', '软件研发类', '苏州', '南京', '无锡', '1');
