INSERT INTO `job_student` VALUES ('1042', '130095050', '周剑寒', 'eed78e005f3d6ea08e67cac3b7625577', '教育技术学(S)', '13教51', '0', '13585201693', '13585201693@163.com', '江苏南京', '南京市玄武区仙鹤茗苑41幢304室', '<p>我叫周剑寒，本科是江苏师范大学教育技术学专业，主修专业课程有C语言、Java语言、摄影技术与艺术、多媒体画面设计、二维动画、三维动画、影视制作、交互多媒体设计等等。</p><p>我已通过国家英语四级考试，有较强的阅读、写作能力。掌握计算机C语言和Java语言的使用，能熟练使用Microsoft Office等常用办公软件以及Adobe Premiere、Adobe Dreamweaver和Photoshop等常用媒体处理软件。</p><p>在大一上学期至大二下学期期间，在院学生会秘书处和生活部工作，具有一定的团队协作能力。<br/></p><p>在2016年3-4月期间曾于徐州市铜山区实验小学海河路校区实习，实习内容主要是讲授信息课、维护学校多媒体设备和录制公开课等。<br/></p><p><br/></p>', '考研', '南京', null, null, '1');
