INSERT INTO `job_student` VALUES ('1245', '140090153', '江雪', '3b6c6e697738d13524ed1e6af5f3fdc4', '', '', '0', '15262053968', '153375303@qq.com', '汉', '江苏省宿迁市', '<p><span style=\"background-color: rgba(255, 255, 255, 0);\">首先感谢您在百忙之中亲阅我这封个人求职自荐信。我叫江雪，是江苏师范学院教育技术学专业的毕业生，。值此择业之际，我怀着一颗赤诚的心和对教育工作的执著追求，真诚地向您推荐我自己。就业在即，投书自荐，我真切地希望能成为贵校的一员，为贵校的发展而作贡献。下面，我将对自己进行一次简单的个人自我介绍：</span></p><p><span style=\"background-color: rgba(255, 255, 255, 0);\">高速发展的计算机业需要新型人才，需要具有创新的技术、专业的知识和富有团队作业能力的人才。四年的计算机专业知识学习和丰富的课余社会实践经历，使我在计算机技术和实践方面获益匪浅。我深知当今社会“非专无以立足，非通无以应变”的道理，立足专业、全面发展、勤恳踏实、积极上进是我四年大学生活的概括和总结。经过四年的刻苦学习，我掌握了比较扎实的基础知识，各科课程都取得了不错的成绩。在计算机专业知识方面，主要具有一定的java编程能力，具有一定的小型信息管理系统开发和组网能力，熟悉办公自动化系统。了解Windows系列操作系统的基本原理，能熟练制作教学课件。同时，还利用课余时间学习了一些机房管理和维护知识。在英语学习方面，我通过了大学英语三级考试，并且在四级考试中也取得了比较好的成绩。但作为一名师范类学生，还必须要具有相关的教育工作知识，对此，我进行过比较系统的教育学和心理学知识的学习，通过了普通话二级甲等考试。且在教育实习中得到了比较好的应用和锻炼，使我在实习工作中得到了老师和同学们的好评，通过这些学习和实践后，使我具备了相当的从事教育工作的知识和能力，为我将来的教师职业打下了良好的基础。</span></p><p style=\"text-align: left;\"><span style=\"background-color: rgba(255, 255, 255, 0);\">我坚信：没有太多经验不要紧，最重要的是只要认真努力不断去学习，就一定能很快胜任新的工作并取得成功。由于我年轻，具有较强吸收新知识的能力，适应现代科技的发展，我将凭借我的敬业精神和勤奋刻苦，为贵校的建设和发展注入新鲜的血液和新动力。我不相信天才，但相信天道酬勤，一分耕耘，一分收获。我相信机会垂青有准备的人，我渴望贵校能够给我这个初入社会满腔热血的年轻人一个机会。也许我还给您的不是奇迹，但肯定会是我所有的勤勉与忠诚!</span></p>', '教师', '徐州', null, null, '1');
